    def __concat__(self, *args, **kwargs):
        '''
        https://pandas.pydata.org/docs/reference/api/pandas.concat.html#pandas.concat
        '''
        objs = kwargs['objs'].sql_to_dataframe()
        axis = 0
        join = 'outer'
        ignore_index = False
        keys = None
        level = None
        names = None
        verify_integrity = False
        sort = False
        copy = True
        if 'axis' in kwargs.keys(): axis = kwargs['axis']
        if 'join' in kwargs.keys(): join = kwargs['join']
        if 'ignore_index' in kwargs.keys(): ignore_index = kwargs['ignore_index']
        if 'keys' in kwargs.keys(): keys = kwargs['keys']
        if 'level' in kwargs.keys(): level = kwargs['level']
        if 'names' in kwargs.keys(): names = kwargs['names']
        if 'verify_integrity' in kwargs.keys(): verify_integrity = kwargs['verify_integrity']
        if 'sort' in kwargs.keys(): sort = kwargs['sort']
        if 'copy' in kwargs.keys(): copy = kwargs['copy']
        res = self.concat(objs = objs, axis = axis, joni = join, ignore_index = ignore_index, keys = keys, level=level, names=names, verify_integrity=verify_integrity, sort=sort, copy=copy)
        return res

